import {
	AGREGAR_PRODUCTO,
	AGREGAR_PRODUCTO_ERROR,
	AGREGAR_PRODUCTO_EXITO,
	COMENZAR_DESCARGA_PRODUCTOS,
	DESCARGA_PRODUCTOS_EXITO,
	DESCARGA_PRODUCTOS_ERROR
} from '../types';
import clienteAxios from '../config/axios';
import Swal from 'sweetalert2';

// Crear nuevos productos

export function crearNuevoProductoAction(producto) {
	return async (dispatch) => {
		dispatch(agregarProducto());
		try {
			// insertar en la api
			await clienteAxios.post('/productos', producto);
			// Si todo sale bindActionCreators, actualiza el state
			dispatch(agregarProductoExito(producto));

			// Alerta
			Swal.fire('Correcto', 'El producto se agreg칩 correctamente', 'success');
			console.log('Producto a침adido');
		} catch (error) {
			console.log('Ha sudecido un error', error);
			// Si hay un error, cambiar el state
			dispatch(agregarProductoError(true));

			Swal.fire({
				icon: 'error',
				title: 'Hubo un error',
				text: 'Hubo un error, intenta de nuevo'
			});
		}
	};
}

const agregarProducto = () => ({
	type: AGREGAR_PRODUCTO,
	payload: true
});

// Si el producto se guarda en base de datos

const agregarProductoExito = (producto) => ({
	type: AGREGAR_PRODUCTO_EXITO,
	payload: producto
});

// Si hubo un error

const agregarProductoError = (estado) => ({
	type: AGREGAR_PRODUCTO_ERROR,
	payload: estado
});




// Funcion que descarga los productos de bd

export function obtenerProdutosAction() {



	return async (dispatch)=>{
		dispatch(descargarProductos())
	}
}
