import React from 'react';
import { Link, useHistory } from 'react-router-dom';
import Swal from 'sweetalert2';

// Redux

import { useDispatch } from 'react-redux';
import { borrarProductoAction, obtenerProductoEditar } from '../actions/productoActions';

const Producto = ({ producto }) => {
	const dispatch = useDispatch();

	const history = useHistory();
	// Confirmar si eliminar

	const confirmarEliminarProducto = (id) => {
		// Preguntar al usuario
		Swal.fire({
			title: 'Are you sure?',
			text: "You won't be able to revert this!",
			icon: 'warning',
			showCancelButton: true,
			confirmButtonColor: '#3085d6',
			cancelButtonColor: '#d33',
			confirmButtonText: 'Yes, delete it!'
		}).then((result) => {
			if (result.value) {
				// Pasarlo al action
				dispatch(borrarProductoAction(id));
			}
		});
	};

	const { nombre, precio, id } = producto;

	// Funcion que redirife de forma programada

	const redireccionarEdicion = (producto) => {
        dispatch(obtenerProductoEditar)
        history.push(`/productos/editar/${producto.id}`);
	};
	return (
		<tr>
			<td>{nombre}</td>
			<td>
				<span className="font-weight-bold">$ {precio}</span>
			</td>
			<td className="acciones">
				<button type="button" onClick={() => redireccionarEdicion(producto)} className="btn btn-primary mr-2">
					Editar
				</button>
				<button type="button" className="btn btn-danger" onClick={() => confirmarEliminarProducto(id)}>
					Eliminar
				</button>
			</td>
		</tr>
	);
};

export default Producto;
